import React, { useState } from 'react';
import { useSpeechSynthesis } from 'react-speech-kit';
import Header from '../Header';
import Footer from '../Footer';

const SrilankanCuisine = () => {
    const { speak } = useSpeechSynthesis();
    const [products, setProducts] = useState([
        {
            id: 1,
            name: "Sri Lankan Rice and Curry",
            price: "$12",
            status: "In Stock",
            imageUrl: "data:image/jpeg;base64,",
        },
        {
            id: 2,
            name: "Kottu Roti",
            price: "$8",
            status: "In Stock",
            imageUrl: "data:image/jpeg;base64,",
        },
        {
            id: 3,
            name: "Idi-Yappa",
            price: "$2",
            status: "In stock",
            imageUrl: "data:image/jpeg;base64,",
        },
        // Add more Sri Lankan food products as needed
    ]);

    const handleClick = (product) => {
        speak({ text: `Product ${product.name}, Price ${product.price}, Status ${product.status}` });
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <Header />
            <div style={{ backgroundColor: '#fffff0', minHeight: '100vh', paddingTop: '50px' }}>
                <h2 style={{ color: '#333', fontSize: '32px', marginBottom: '20px', fontFamily: 'sans-serif' }}>Sri Lankan Foods</h2>


                <div style={{ display: 'flex', justifyContent: 'center' }}>
                    {products.map((product, index) => (
                        <div key={product.id} style={{ margin: '20px' }}>
                            <img src={product.imageUrl} alt={product.name} style={{ width: '350px', height: '350px', marginBottom: '10px' }} />
                            <div>
                                <h3>{product.name}</h3>
                                <p>Price: {product.price}</p>
                                <p>Status: {product.status}</p>
                                <button onClick={() => handleClick(product)}>Speak</button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default SrilankanCuisine;
